package io.jiantao.utils.java;import java.text.DecimalFormat;/** * Created by Jiantao.Yang on 2019-12-17. */public class FormatUtils {    /**     * 格式化字节速度大小     * @param speed byte/ms     * @return 返回（xKB/s or xMB/s）     */    public static String getSpeedStr(float speed) {        if (speed <= 0) {            return "0B/s";        }        DecimalFormat decimalFormat = new DecimalFormat("#0.00");        float kbs = speed * 1000 / 1024;        if (kbs > 1024) {            float mbs = kbs / 1024;            return String.valueOf(decimalFormat.format(mbs)).concat("MB/s");        }        return String.valueOf(decimalFormat.format(kbs)).concat("KB/s");    }}